const t=document.querySelector("button"),c=document.querySelector("#menu");t?.addEventListener("click",function(){c?.classList.toggle("active"),t.querySelectorAll("div").forEach(e=>{e.classList.toggle("open-menu")})});c?.querySelectorAll(".menu__links__item a").forEach(e=>{e.addEventListener("click",function(){c.classList.remove("active"),t?.querySelectorAll("div").forEach(n=>{n.classList.toggle("open-menu")})})});
